import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../../hooks/useAuth';
import { 
  Package, User, Mail, Phone, MapPin, 
  Upload, Calculator, AlertCircle, Loader 
} from 'lucide-react';

const CreateCourierForm = () => {
  const { user, getToken } = useAuth();
  const [formData, setFormData] = useState({
    semail: user?.email || '',
    remail: '',
    sname: user?.name || '',
    rname: '',
    sphone: user?.pnumber || '',
    rphone: '',
    saddress: user?.address || '',
    raddress: '',
    weight: '',
    image: null
  });

  const [price, setPrice] = useState(null);
  const [loading, setLoading] = useState(false);
  const [calculating, setCalculating] = useState(false);
  const [error, setError] = useState('');
  const [imagePreview, setImagePreview] = useState(null);
  const navigate = useNavigate();

  // Calculate price when weight changes
  useEffect(() => {
    const calculatePrice = async () => {
      if (!formData.weight || parseFloat(formData.weight) <= 0) return;
      
      try {
        setCalculating(true);
        const response = await fetch('http://localhost:5000/api/calculate-price', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({
            weight: parseFloat(formData.weight),
            zone: 'default'
          })
        });
        
        if (!response.ok) {
          const data = await response.json();
          throw new Error(data.message || 'Failed to calculate price');
        }

        const data = await response.json();
        setPrice(data.price);
      } catch (err) {
        console.error('Price calculation error:', err);
        setError('Failed to calculate price. Please try again.');
      } finally {
        setCalculating(false);
      }
    };

    const debounceTimeout = setTimeout(() => {
      if (formData.weight) {
        calculatePrice();
      }
    }, 500);

    return () => clearTimeout(debounceTimeout);
  }, [formData.weight]);

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (!file) return;

    // Validate file size (5MB limit)
    if (file.size > 5 * 1024 * 1024) {
      setError('Image size must be less than 5MB');
      return;
    }

    // Validate file type
    if (!file.type.startsWith('image/')) {
      setError('Please upload an image file');
      return;
    }

    setFormData(prev => ({ ...prev, image: file }));
    setError('');

    // Create preview
    const reader = new FileReader();
    reader.onloadend = () => {
      setImagePreview(reader.result);
    };
    reader.readAsDataURL(file);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    try {
      // Validate weight
      if (!formData.weight || parseFloat(formData.weight) <= 0) {
        throw new Error('Please enter a valid weight');
      }

      const token = getToken();
      if (!token) {
        throw new Error('Please login to create a shipment');
      }

      const formDataToSend = new FormData();
      
      // Add all text fields
      Object.keys(formData).forEach(key => {
        if (key !== 'image' && formData[key] !== null && formData[key] !== '') {
          formDataToSend.append(key, formData[key]);
        }
      });

      // Add image if exists
      if (formData.image) {
        formDataToSend.append('image', formData.image);
      }

      const response = await fetch('http://localhost:5000/api/courier', {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${token}`
        },
        body: formDataToSend
      });

      const data = await response.json();

      if (!response.ok) {
        throw new Error(data.message || 'Failed to create shipment');
      }

      // Navigate to courier details page
      navigate(`/courier/${data.courier.id}`);
    } catch (err) {
      setError(err.message);
      console.error('Shipment creation error:', err);
    } finally {
      setLoading(false);
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
    if (error) setError('');
  };

  const renderInputField = ({ 
    label, name, type = 'text', value, icon: Icon, required = true, textarea = false 
  }) => (
    <div>
      <label className="block text-sm font-medium text-gray-700">
        {label}
      </label>
      <div className="mt-1 relative">
        {textarea ? (
          <textarea
            name={name}
            value={value}
            onChange={handleChange}
            required={required}
            rows="2"
            className="appearance-none block w-full px-3 py-2 pl-10 border border-gray-300 rounded-md shadow-sm focus:ring-blue-500 focus:border-blue-500"
          />
        ) : (
          <input
            type={type}
            name={name}
            value={value}
            onChange={handleChange}
            required={required}
            className="appearance-none block w-full px-3 py-2 pl-10 border border-gray-300 rounded-md shadow-sm focus:ring-blue-500 focus:border-blue-500"
          />
        )}
        <Icon className="absolute left-3 top-2.5 h-5 w-5 text-gray-400" />
      </div>
    </div>
  );

  return (
    <div className="max-w-4xl mx-auto bg-white rounded-lg shadow-sm p-6">
      <div className="flex items-center gap-2 mb-6">
        <Package className="h-6 w-6 text-blue-600" />
        <h1 className="text-2xl font-bold text-gray-900">Create New Shipment</h1>
      </div>

      {error && (
        <div className="mb-6 flex items-center gap-2 p-4 bg-red-50 text-red-600 rounded-md">
          <AlertCircle className="h-5 w-5 shrink-0" />
          <p>{error}</p>
        </div>
      )}

      <form onSubmit={handleSubmit} className="space-y-8">
        {/* Sender Information */}
        <div className="bg-gray-50 p-6 rounded-lg">
          <h2 className="text-lg font-semibold mb-4">Sender Information</h2>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            {renderInputField({
              label: 'Name',
              name: 'sname',
              value: formData.sname,
              icon: User
            })}
            {renderInputField({
              label: 'Email',
              name: 'semail',
              type: 'email',
              value: formData.semail,
              icon: Mail
            })}
            {renderInputField({
              label: 'Phone',
              name: 'sphone',
              type: 'tel',
              value: formData.sphone,
              icon: Phone
            })}
            {renderInputField({
              label: 'Address',
              name: 'saddress',
              value: formData.saddress,
              icon: MapPin,
              textarea: true
            })}
          </div>
        </div>

        {/* Receiver Information */}
        <div className="bg-gray-50 p-6 rounded-lg">
          <h2 className="text-lg font-semibold mb-4">Receiver Information</h2>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            {renderInputField({
              label: 'Name',
              name: 'rname',
              value: formData.rname,
              icon: User
            })}
            {renderInputField({
              label: 'Email',
              name: 'remail',
              type: 'email',
              value: formData.remail,
              icon: Mail
            })}
            {renderInputField({
              label: 'Phone',
              name: 'rphone',
              type: 'tel',
              value: formData.rphone,
              icon: Phone
            })}
            {renderInputField({
              label: 'Address',
              name: 'raddress',
              value: formData.raddress,
              icon: MapPin,
              textarea: true
            })}
          </div>
        </div>

        {/* Package Information */}
        <div className="bg-gray-50 p-6 rounded-lg">
          <h2 className="text-lg font-semibold mb-4">Package Information</h2>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            <div>
              <label className="block text-sm font-medium text-gray-700">
                Weight (kg)
              </label>
              <div className="mt-1 relative">
                <input
                  type="number"
                  name="weight"
                  step="0.01"
                  min="0"
                  value={formData.weight}
                  onChange={handleChange}
                  required
                  className="appearance-none block w-full px-3 py-2 pl-10 border border-gray-300 rounded-md shadow-sm focus:ring-blue-500 focus:border-blue-500"
                />
                <Calculator className="absolute left-3 top-2.5 h-5 w-5 text-gray-400" />
              </div>
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700">
                Package Image
              </label>
              <div className="mt-1 flex items-center space-x-4">
                <label className="relative cursor-pointer bg-white py-2 px-4 border border-gray-300 rounded-md shadow-sm font-medium text-blue-600 hover:text-blue-500 focus-within:outline-none focus-within:ring-2 focus-within:ring-offset-2 focus-within:ring-blue-500">
                  <span className="flex items-center">
                    <Upload className="h-5 w-5 mr-2" />
                    Choose file
                  </span>
                  <input
                    type="file"
                    className="sr-only"
                    accept="image/*"
                    onChange={handleImageChange}
                  />
                </label>
                {imagePreview && (
                  <img
                    src={imagePreview}
                    alt="Package preview"
                    className="h-16 w-16 object-cover rounded-md"
                  />
                )}
              </div>
            </div>
          </div>

          {calculating ? (
            <div className="mt-4 p-4 bg-blue-50 rounded-md flex items-center gap-2">
              <Loader className="h-5 w-5 animate-spin text-blue-600" />
              <span className="text-blue-600">Calculating price...</span>
            </div>
          ) : price !== null && (
            <div className="mt-4 p-4 bg-blue-50 rounded-md">
              <h3 className="text-lg font-medium text-blue-900">
                Estimated Price
              </h3>
              <p className="mt-1 text-2xl font-bold text-blue-600">
                â‚¹{price.toFixed(2)}
              </p>
            </div>
          )}
        </div>

        <div className="flex justify-end">
          <button
            type="submit"
            disabled={loading || calculating}
            className={`flex items-center gap-2 px-6 py-3 border border-transparent text-base font-medium rounded-md shadow-sm text-white bg-blue-600 hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500 ${
              (loading || calculating) ? 'opacity-75 cursor-not-allowed' : ''
            }`}
          >
            {loading && <Loader className="h-5 w-5 animate-spin" />}
            {loading ? 'Creating Shipment...' : 'Create Shipment'}
          </button>
        </div>
      </form>
    </div>
  );
};

export default CreateCourierForm;
